    "lib/util.coffee.md": {
      "path": "lib/util.coffee.md",
      "content": "Util\n====\n\n    module.exports =\n      applyStylesheet: (style, id=\"primary\") ->\n        styleNode = document.createElement(\"style\")\n        styleNode.innerHTML = style\n        styleNode.id = id\n\n        if previousStyleNode = document.head.querySelector(\"style##{id}\")\n          previousStyleNode.parentNode.removeChild(prevousStyleNode)\n\n        document.head.appendChild(styleNode)\n",
      "mode": "100644",
      "type": "blob"
    },
    "lib/viz.coffee.md": {
      "path": "lib/viz.coffee.md",
      "content": "Audio Viz\n=========\n\n    module.exports = (analyser) ->\n      bins = analyser.frequencyBinCount\n      frequencyDomain = new Uint8Array(bins)\n      timeDomain = new Uint8Array(bins)\n\n      draw: (canvas) ->\n        analyser.getByteFrequencyData(frequencyDomain)\n        analyser.getByteTimeDomainData(timeDomain)\n\n        canvas.fill \"black\"\n\n        width = canvas.width()\n        height = canvas.height()\n        ctx = canvas.context()\n        ratio = height / 256\n        step = width / bins\n\n        ctx.fillStyle = \"#00F\"\n\n        ctx.beginPath()\n        ctx.moveTo(0, height)\n\n        # Draw waveforms or frequency spectrum\n        Array::forEach.call frequencyDomain, (value, index) ->\n          x = index * step\n          y = ratio * (256 - value)\n\n          ctx.lineTo x, y\n\n        ctx.lineTo(width, height)\n        ctx.fill()\n\n        ctx.lineWidth = 2\n        ctx.strokeStyle = \"#F00\"\n\n        Array::forEach.call timeDomain, (value, index) ->\n          x = index * step\n          y = ratio * (256 - value)\n\n          if index is 0\n            ctx.beginPath()\n            ctx.moveTo x, y\n          else\n            ctx.lineTo x, y\n\n        ctx.stroke()\n",
      "mode": "100644",
      "type": "blob"
    },
    "main.coffee": {
      "path": "main.coffee",
      "content": "\nglobal.Observable = require \"observable\"\n\ngetUserMedia = require \"./lib/get_user_media\"\nAudioContext = window.AudioContext or window.webkitAudioContext\nModel = require(\"./model\")\nViz = require \"./lib/viz\"\n\n{applyStylesheet} = require \"./lib/util\"\napplyStylesheet require \"./style\"\n\ncreateAudio = (stream) ->\n  context = new AudioContext\n  microphone = context.createMediaStreamSource(stream)\n\n  global.model = Model\n    input: microphone\n\n  document.body.appendChild require(\"./template\")(model)\n\n  canvas = createCanvas()\n\n  analyser = context.createAnalyser()\n  analyser.smoothingTimeConstant = 0\n\n  viz = Viz(analyser)\n\n  draw = ->\n    viz.draw(canvas)\n    requestAnimationFrame draw\n\n  requestAnimationFrame draw\n\n  microphone.connect(analyser)\n\ncreateCanvas = ->\n  Canvas = require \"pixie-canvas\"\n\n  canvas = Canvas()\n\n  handleResize =  ->\n    canvas.width(window.innerWidth)\n    canvas.height(window.innerHeight)\n\n  handleResize()\n  window.addEventListener \"resize\", handleResize, false\n\n  document.body.appendChild canvas.element()\n\n  return canvas\n\nerror = ->\n  console.log arguments\n\nif PACKAGE.name is \"ROOT\"\n  getUserMedia({audio: true}, createAudio, error)\n\n# -------------------------------------------------\n# From here on down is our Whimsy.space integration\n\n\nPostmaster = require(\"postmaster\")\npostmaster = Postmaster\n  save: ->\n    model.asBlob()\n    .then (blob)->\n      filePath = prompt \"File name:\", \"sound.wav\"\n\n      postmaster.invokeRemote \"saveFile\", blob, filePath\n\n# Apps must call childLoaded if they want to receive state/file data from OS\npostmaster.invokeRemote \"childLoaded\"\n\ndocument.addEventListener \"keydown\", (e) ->\n  if e.ctrlKey\n    if e.keyCode is 83 # s\n      e.preventDefault()\n\n      if e.shiftKey\n        newPath = prompt \"Path\", filePath\n        setPath(newPath) if newPath\n\n      postmaster.save()\n",
      "mode": "100644",
      "type": "blob"
    },
    "model.coffee": {
      "path": "model.coffee",
      "content": "Recorder = require \"./lib/recorder\"\nsaveAs = require \"./lib/file_saver\"\n\nif localStorage.TRINKET_POLICY\n  trinket = S3Trinket(JSON.parse(localStorage.TRINKET_POLICY))\n\nmodule.exports = ({input}) ->\n  recorder = new Recorder(input)\n\n  player = document.createElement \"audio\"\n\n  self =\n    recordingClass: ->\n      if self.recording()\n        \"recording\"\n\n    recording: Observable false\n\n    play: ->\n      self.recording false\n      recorder.exportWAV (blob) ->\n        player.src = URL.createObjectURL(blob)\n        player.play()\n\n    record: ->\n      self.recording !self.recording()\n      recorder.clear() if self.recording()\n\n    stop: ->\n      self.recording false\n\n    asBlob: ->\n      new Promise (resolve, reject) ->\n        recorder.exportWAV resolve\n\n  self.recording.observe (newValue) ->\n    if newValue\n      recorder.record()\n    else\n      recorder.stop()\n\n  return self\n",
      "mode": "100644",
      "type": "blob"
    },
