        "README.md": {
          "path": "README.md",
          "content": "# system-client\nClient lib to run apps on ZineOS and proxy messages to the OS through Postmaster\n",
          "mode": "100644",
          "type": "blob"
        },
        "lib/extensions.coffee": {
          "path": "lib/extensions.coffee",
          "content": "# Add some utility readers to the Blob API\nBlob::readAsText = ->\n  file = this\n\n  new Promise (resolve, reject) ->\n    reader = new FileReader\n    reader.onload = ->\n      resolve reader.result\n    reader.onerror = reject\n    reader.readAsText(file)\n\nBlob::getURL = ->\n  Promise.resolve URL.createObjectURL(this)\n\nBlob::readAsJSON = ->\n  @readAsText()\n  .then JSON.parse\n\nBlob::readAsDataURL = ->\n  file = this\n\n  new Promise (resolve, reject) ->\n    reader = new FileReader\n    reader.onload = ->\n      resolve reader.result\n    reader.onerror = reject\n    reader.readAsDataURL(file)\n\n# Load an image from a blob returning a promise that is fulfilled with the\n# loaded image or rejected with an error\nImage.fromBlob = (blob) ->\n  blob.getURL()\n  .then (url) ->\n    new Promise (resolve, reject) ->\n      img = new Image\n      img.onload = ->\n        resolve img\n      img.onerror = reject\n\n      img.src = url\n",
          "mode": "100644",
          "type": "blob"
        },
        "lib/file-io.coffee": {
          "path": "lib/file-io.coffee",
          "content": "# Handle basic file saving/loading/picking, displaying modals/ui.\n\n# Host must provide the following methods\n#   `loadFile` Take a blob and path and load it as the application state.\n#   `saveData` Return a promise that will be fulfilled with a blob of the\n#     current application state.\n#   `newFile` Initialize the application to an empty state.\n\nmodule.exports = (client) ->\n  {system, Observable, UI} = client\n  {Modal} = UI\n\n  currentPath = Observable \"\"\n  saved = Observable true\n\n  confirmUnsaved = ->\n    return Promise.resolve() if saved()\n\n    new Promise (resolve, reject) ->\n      Modal.confirm \"You will lose unsaved progress, continue?\"\n      .then (result) ->\n        if result\n          resolve()\n        else\n          reject()\n\n  self =\n    currentPath: currentPath\n    saved: saved\n    new: ->\n      if saved()\n        currentPath \"\"\n        self.newFile()\n      else\n        confirmUnsaved()\n        .then ->\n          saved true\n          self.newFile()\n\n    open: ->\n      confirmUnsaved()\n      .then  ->\n        # TODO: File browser\n        Modal.prompt \"File Path\", currentPath()\n        .then (newPath) ->\n          if newPath\n            currentPath newPath\n          else\n            throw new Error \"No path given\"\n        .then (path) ->\n          system.readFile path, true\n          .then (file) ->\n            self.loadFile file, path\n\n    save: ->\n      if currentPath()\n        self.saveData()\n        .then (blob) ->\n          system.writeFile currentPath(), blob, true\n        .then ->\n          saved true\n          currentPath()\n      else\n        self.saveAs()\n\n    saveAs: ->\n      Modal.prompt \"File Path\", currentPath()\n      .then (path) ->\n        if path\n          currentPath path\n          self.save()\n\n  return self\n",
          "mode": "100644",
          "type": "blob"
        },
        "main.coffee": {
          "path": "main.coffee",
          "content": "# system-client is what prepares the environment for user apps\n# we hook up the postmaster and proxy messages to the OS\n# we also provide system packages for the application to use like UI\n\n{version} = require \"./pixie\"\n\nSystemClient = ->\n  # NOTE: These required packages get populated from the parent package when building\n  # the runnable app. See util.coffee\n  Postmaster = require \"postmaster\"\n  UI = require \"ui\"\n\n  style = document.createElement \"style\"\n  style.innerHTML = UI.Style.all\n  document.head.appendChild style\n\n  postmaster = Postmaster()\n  remoteExists = postmaster.remoteTarget()\n\n  applicationProxy = new Proxy {}\n  ,\n    get: (target, property, receiver) ->\n      target[property] or\n      ->\n        postmaster.invokeRemote \"application\", property, arguments...\n\n  systemProxy = new Proxy\n    ready: ->\n      if remoteExists\n        postmaster.invokeRemote \"ready\",\n          ZineOSClient: version\n        .then (result) ->\n          console.log result\n          appData = result?.ZineOS\n  \n          return appData\n      else # Quick fail when there is no parent window to connect to\n        Promise.reject \"No parent window\"\n  ,\n    get: (target, property, receiver) ->\n      target[property] or\n      ->\n        postmaster.invokeRemote \"system\", property, arguments...\n\n  # TODO: Also interesting would be to proxy observable arguments where we\n  # create the receiver on the opposite end of the membrane and pass messages\n  # back and forth like magic\n\n  document.addEventListener \"mousedown\", ->\n    if remoteExists\n      applicationProxy.raiseToTop()\n      .catch console.warn\n\n  system: systemProxy\n  application: applicationProxy\n  postmaster: postmaster\n  util:\n    FileIO: require \"./lib/file-io\"\n  Observable: UI.Observable\n  UI: UI\n  version: version\n\nSystemClient.applyExtensions = ->\n  require \"./lib/extensions\"\n\nmodule.exports = SystemClient\n",
          "mode": "100644",
          "type": "blob"
        },
        "pixie.cson": {
          "path": "pixie.cson",
          "content": "version: \"0.1.3\"\npublish:\n  s3:\n    basePath: \"public/danielx.net\"\ndependencies:\n  postmaster: \"distri/postmaster:master\"\n  ui: \"STRd6/ui:master\"\n",
          "mode": "100644",
          "type": "blob"
        },
        "test/main.coffee": {
          "path": "test/main.coffee",
          "content": "mocha.globals(['OBSERVABLE_ROOT_HACK'])\n\nSystemClient = require \"../main\"\n\ndescribe \"System Client\", ->\n  it \"should extend native Blob APIs when applying extensions\", ->\n    assert !Blob::readAsText\n\n    SystemClient.applyExtensions()\n    assert Blob::readAsText\n\n  it \"should return system and application proxies\", ->\n    {system, application} = SystemClient()\n\n    assert system\n    assert application\n\n  it \"should provide FileIO as a util\", ->\n    {util} = client = SystemClient()\n\n    assert util.FileIO\n\n    handlers = util.FileIO(client)\n\n    assert.equal handlers.currentPath(), \"\"\n\n  it \"should connect when ready is called\", (done) ->\n    {system, application} = SystemClient()\n\n    system.ready()\n    .catch (e) ->\n      console.warn e\n      done()\n\n    return\n",
          "mode": "100644",
          "type": "blob"
        }
