          "content": "(function() {\n  var SystemClient, version,\n    __slice = [].slice;\n\n  version = require(\"./pixie\").version;\n\n  SystemClient = function() {\n    var Postmaster, UI, applicationProxy, postmaster, remoteExists, style, systemProxy;\n    Postmaster = require(\"postmaster\");\n    UI = require(\"ui\");\n    style = document.createElement(\"style\");\n    style.innerHTML = UI.Style.all;\n    document.head.appendChild(style);\n    postmaster = Postmaster();\n    remoteExists = postmaster.remoteTarget();\n    applicationProxy = new Proxy({}, {\n      get: function(target, property, receiver) {\n        return target[property] || function() {\n          return postmaster.invokeRemote.apply(postmaster, [\"application\", property].concat(__slice.call(arguments)));\n        };\n      }\n    });\n    systemProxy = new Proxy({\n      ready: function() {\n        if (remoteExists) {\n          return postmaster.invokeRemote(\"ready\", {\n            ZineOSClient: version\n          }).then(function(result) {\n            var appData;\n            console.log(result);\n            appData = result != null ? result.ZineOS : void 0;\n            return appData;\n          });\n        } else {\n          return Promise.reject(\"No parent window\");\n        }\n      }\n    }, {\n      get: function(target, property, receiver) {\n        return target[property] || function() {\n          return postmaster.invokeRemote.apply(postmaster, [\"system\", property].concat(__slice.call(arguments)));\n        };\n      }\n    });\n    document.addEventListener(\"mousedown\", function() {\n      if (remoteExists) {\n        return applicationProxy.raiseToTop()[\"catch\"](console.warn);\n      }\n    });\n    return {\n      system: systemProxy,\n      application: applicationProxy,\n      postmaster: postmaster,\n      util: {\n        FileIO: require(\"./lib/file-io\")\n      },\n      Observable: UI.Observable,\n      UI: UI,\n      version: version\n    };\n  };\n\n  SystemClient.applyExtensions = function() {\n    return require(\"./lib/extensions\");\n  };\n\n  module.exports = SystemClient;\n\n}).call(this);\n",
